import { useState } from "react"


const App = () => {
  // const [left, setLeft] = useState(0)
  // const [right, setRight] = useState(0)
  const [clicks,setClicks] = useState(0)
  const handleLeft = () =>{
    const newClicks={
      left:clicks.left+1,
      right:clicks.right
    }
    setClicks(newClicks)
  }

  const handleRight = () =>{
    const newClicks={
      left:clicks.left,
      right:clicks.right+1
    }
    setClicks(newClicks)
  }



  return (
    <div>
      {clicks.left}
      <button onClick={handleLeft}>
        left
      </button>
      <button onClick={handleRight}>
        right
      </button>
      {clicks.right}
    </div>
  )
}

export default App
