  return <h2 key={partid}>total of {parts.reduce((sum,part) => sum+part.exercises,0)} exercises </h2>
}

const Header = ({courses}) => {
  return(
    <>
    {courses.map(course => <Course key={course.id} coursename={course.name} />)}
    {courses.map(course => <Total key={course.id} parts={course.parts}/>)}
    </>
  )
}


